var ToDoApp={};$(document).ready(function(){var e=new Storage("todoList");e.getValue()||e.initialize(),$(".submit_form").on("click",function(t){t.preventDefault();var i=$(".text_box").val();e.putValue(i),$(".list").append("<li> <span>"+i+'</span> <img id="deleteImg" src="images/delete.png" /> </li>')}),$(document).on("click","li",function(){$(this).remove(),e.removeValue(this.innerText.trim())})});