var LocalStorage=require("node-localstorage").LocalStorage,localStorage=new LocalStorage("./todo"),Storage=function(e){this.key=e,this.saveValue=function(e){var t=JSON.parse(localStorage.getItem(this.key));return t.push(e),localStorage.setItem(this.key,JSON.stringify(t))},this.getValue=function(){return JSON.parse(localStorage.getItem(this.key))},this.initialize=function(){return localStorage.setItem(this.key,JSON.stringify([]))},this.removeValue=function(e){var t=this.getValue(),a=t.indexOf(e);if(a!==-1)return t.splice(a,1),localStorage.setItem(this.key,JSON.stringify(t))}};module.exports=Storage;